const items = $input.all();
const output = [];

for (const item of items) {
  const email = item.json.Email || item.json.email || "";

  let firstName = null;

  if (email && email.includes("@")) {
    const beforeAt = email.split("@")[0];

    // ŸÜÿßÿÆÿØ ÿ£ŸàŸÑ ÿ¨ÿ≤ÿ° ŸÇÿ®ŸÑ ÿ£Ÿä ÿ±ŸÇŸÖ ÿ£Ÿà underscore
    const match = beforeAt.match(/[a-zA-Z]+/);

    if (match) {
      firstName = match[0].toUpperCase();
    }
  }

  output.push({
    json: {
      email: email,
      first_name: firstName
    }
  });
}

return output;

const items = $input.all();
const output = [];

const positiveWords = [
  "excellent", "amazing", "perfect", "love", "great", "delicious", "awesome", "good", "nice"
];

const negativeWords = [
  "very bad", "terrible", "awful", "worst", "hate",
  "bad", "cold", "late", "poor", "disappointed"
];

for (const item of items) {
  const reviewTextRaw = item.json["Enter your review"] ?? "";
  const reviewText = String(reviewTextRaw).toLowerCase();

  let positiveScore = 0;
  let negativeScore = 0;

  // ÿßÿ≠ÿ≥ÿ® ÿßŸÑÿ≥ÿßŸÑÿ®
  for (const w of negativeWords) {
    if (reviewText.includes(w)) negativeScore++;
  }

  // ÿßÿ≠ÿ≥ÿ® ÿßŸÑŸÖŸàÿ¨ÿ®
  for (const w of positiveWords) {
    if (reviewText.includes(w)) positiveScore++;
  }

  let sentiment = "neutral";
  let responseMessage =
    "Thank you for sharing your feedback with us. We appreciate your time and will review your comments carefully.";

  // ‚úÖ ŸÇÿ±ÿßÿ± ÿßŸÑÿ™ÿµŸÜŸäŸÅ (ÿ£Ÿä ÿ≥ÿßŸÑÿ® ŸÖŸàÿ¨ŸàÿØ = ÿ≥ŸÑÿ®Ÿä)
  if (negativeScore > 0 && negativeScore >= positiveScore) {
    sentiment = "negative";
    responseMessage =
      "We sincerely apologize for your experience. Your feedback is important to us, and we will review this carefully to improve.";
  } else if (positiveScore > 0 && positiveScore > negativeScore) {
    sentiment = "positive";
    responseMessage =
      "Thank you so much for your kind feedback! We‚Äôre happy you enjoyed your experience and look forward to serving you again.";
  }

  output.push({
    json: {
      review_text: reviewTextRaw,
      review_sentiment: sentiment,
      response_message: responseMessage,
      debug: { positiveScore, negativeScore } // üëà ŸÖŸÅŸäÿØ ŸÑŸÑÿ™ÿ£ŸÉÿØ
    }
  });
}

return output;

